function memoize(fn) {
  const cache = {};
  return function(...args) {
    const key = JSON.stringify(args);
    if (cache[key]) return cache[key]; // return cached
    cache[key] = fn(...args);          // compute & store
    return cache[key];
  };
}
const slowFn = memoize(n => n * n);
slowFn(4); // computes: 16
slowFn(4); // from cache: 16

