-- Oracle specific: DUAL table for expressions
SELECT SYSDATE FROM DUAL;
SELECT 2 + 2 FROM DUAL;

-- Oracle ROWNUM (different from MySQL LIMIT)
SELECT * FROM employees WHERE ROWNUM <= 10;

-- Oracle doesn't support LIMIT directly:
-- Use FETCH (12c+)
SELECT * FROM employees
FETCH FIRST 10 ROWS ONLY;

-- Create sequence
CREATE SEQUENCE emp_id_seq
  START WITH 1
  INCREMENT BY 1
  NOCACHE
  NOCYCLE;

-- Use sequence
INSERT INTO employees (emp_id, first_name)
VALUES (emp_id_seq.NEXTVAL, 'Alice');

-- Get current value (must call NEXTVAL first)
SELECT emp_id_seq.CURRVAL FROM DUAL;

-- Oracle 12c+ Identity Column (simpler)
CREATE TABLE employees (
  emp_id NUMBER GENERATED ALWAYS AS IDENTITY,
  name   VARCHAR2(100)
);

-- NVL: if commission is NULL, use 0
SELECT first_name, 
       salary + NVL(commission, 0) AS total_comp
FROM employees;

-- NVL2: not null → value1, null → value2
SELECT first_name,
       NVL2(commission, 'Has Commission', 'No Commission') AS status
FROM employees;

-- COALESCE: returns first non-null (standard SQL)
SELECT COALESCE(phone, mobile, email, 'No contact') FROM contacts;

DECLARE
  v_name    VARCHAR2(100);
  v_salary  NUMBER;
BEGIN
  SELECT first_name, salary
  INTO v_name, v_salary
  FROM employees
  WHERE emp_id = 101;

  DBMS_OUTPUT.PUT_LINE('Employee: ' || v_name);
  DBMS_OUTPUT.PUT_LINE('Salary: ' || v_salary);

EXCEPTION
  WHEN NO_DATA_FOUND THEN
    DBMS_OUTPUT.PUT_LINE('Employee not found');
  WHEN OTHERS THEN
    DBMS_OUTPUT.PUT_LINE('Error: ' || SQLERRM);
END;
/

-- B-Tree index (most common)
CREATE INDEX idx_emp_lastname ON employees(last_name);

-- Unique index
CREATE UNIQUE INDEX idx_emp_email ON employees(email);

-- Composite index (column order matters!)
CREATE INDEX idx_emp_dept_sal ON employees(department_id, salary);

-- Function-based index
CREATE INDEX idx_emp_upper_name ON employees(UPPER(last_name));
-- Now this query uses index:
SELECT * FROM employees WHERE UPPER(last_name) = 'SMITH';

-- Bitmap (for low cardinality like gender, status)
CREATE BITMAP INDEX idx_emp_gender ON employees(gender);

-- Stored Procedure (action, no return)
CREATE OR REPLACE PROCEDURE update_salary(
  p_emp_id IN NUMBER,
  p_percent IN NUMBER,
  p_new_salary OUT NUMBER  -- OUT parameter
) AS
BEGIN
  UPDATE employees SET salary = salary * (1 + p_percent/100)
  WHERE emp_id = p_emp_id
  RETURNING salary INTO p_new_salary;
  COMMIT;
END;

-- Function (returns value, can use in SELECT)
CREATE OR REPLACE FUNCTION get_full_name(p_emp_id NUMBER)
RETURN VARCHAR2 AS
  v_name VARCHAR2(200);
BEGIN
  SELECT first_name || ' ' || last_name INTO v_name
  FROM employees WHERE emp_id = p_emp_id;
  RETURN v_name;
END;

-- Use in SQL:
SELECT get_full_name(101) FROM DUAL;

-- Audit trigger: log all changes
CREATE OR REPLACE TRIGGER emp_audit_trg
AFTER INSERT OR UPDATE OR DELETE ON employees
FOR EACH ROW
BEGIN
  IF INSERTING THEN
    INSERT INTO audit_log VALUES ('INSERT', :NEW.emp_id, SYSDATE, USER);
  ELSIF UPDATING THEN
    INSERT INTO audit_log VALUES ('UPDATE', :OLD.emp_id, SYSDATE, USER);
  ELSIF DELETING THEN
    INSERT INTO audit_log VALUES ('DELETE', :OLD.emp_id, SYSDATE, USER);
  END IF;
END;
/
-- :NEW = new values, :OLD = old values

